import React from "react";
import { Redirect } from "react-router-dom";

const useAuth = () => {
  const user = localStorage.getItem("token");
  return user;
};
const ProtectedRoutes = (props) => {
  const isAuth = useAuth();
  return isAuth ? (
    props.children
  ) : (
    <Redirect
      to={{
        pathname: "/argon/login-page",
      }}
    />
  );
};

export default ProtectedRoutes;
