import React from "react";
import { Button } from "reactstrap";
import axios from "axios";
import { useHistory } from "react-router-dom";
import LoginGithub from "react-login-github";

const GithubLogin = () => {
  const history = useHistory();
  const clientId = process.env.REACT_APP_GITHUB_CLIENT_ID;
  const client_secret = process.env.REACT_APP_GITHUB_CLIENT_SECRET;
  const redirect_uri = process.env.REACT_APP_GITHUB_REDIRECT_URI;

  const onSuccess = (response) => {
    console.log(response);
    // const code = response.code;
    // let data = { client_id: clientId, client_secret, code };

    axios
      .get(
        `https://github.com/login/oauth/authorize?scope=user&client_id=${clientId}&redirect_uri=${redirect_uri}`
      )
      .then((res) => {
        console.log("first res", res);
        // axios
        //   .post("https://github.com/login/oauth/access_token", res)
        //   .then((resp) => console.log("seconde res", resp));
      })
      .catch((err) => console.log(err));
    // window.open("http://localhost:5000/auth/github", "_self");
  };

  const onFailure = (response) => console.error(response);

  return (
    // <LoginGithub
    //   clientId={clientId}
    //   onSuccess={onSuccess}
    //   onFailure={onFailure}
    // />
    <Button
      className="btn-neutral btn-icon"
      color="default"
      // href={`https://github.com/login/oauth/authorize?scope=user&client_id=${clientId}&redirect_uri=${redirect_uri}`}
      onClick={onSuccess}
    >
      <span className="btn-inner--icon">
        <img
          alt="..."
          src={require("assets/img/icons/common/github.svg")}
        ></img>
      </span>
      <span className="btn-inner--text">Github</span>
    </Button>
  );
};

export default GithubLogin;
